import React, {FormEvent, useState} from 'react';

import PageHeader from '../../components/PageHeader';
import PeopleItem from '../../components/PeopleItem';
import Select from '../../components/Select';
import Input from "../../components/Input";

import './styles.css';
import api from '../../services/api';
import {People} from '../../components/PeopleItem';


function Peoples() {
    const [peoples, setPeoples] = useState([])

    const [preference, setPreference] = useState('');
    const [language, setLanguage] = useState('');
    const [maxAge, setMaxAge] = useState('');

    async function searchPeople(e : FormEvent) {
        e.preventDefault();

        const response = await api.get('users', {
            params: {
                preference
            }
        })

        setPeoples(response.data)
    }

    return (
        <div id="page-teacher-list" className="container">
            <PageHeader title="O amor da sua vida estÃ¡ por aqui">
                <form id="search-people"
                    onSubmit={searchPeople}>

                    <Select name="preference" label="Sexualidade"
                        value={preference}
                        onChange={
                            (e) => {
                                setPreference(e.target.value)
                            }
                        }
                        options={
                            [
                                {
                                    value: "Homem",
                                    label: "Homem"
                                },
                                {
                                    value: "Mulher",
                                    label: "Mulher"
                                },
                                {
                                    value: "Ambos",
                                    label: "Ambos"
                                },
                                {
                                    value: "Outros",
                                    label: "Outros"
                                }
                            ]
                        }/>

                    <Select name="language" label="Linguagem"
                        value={language}
                        onChange={
                            (e) => {
                                setLanguage(e.target.value)
                            }
                        }
                        options={
                            [
                                {
                                    value: "Homem",
                                    label: "Homem"
                                },
                                {
                                    value: "Mulher",
                                    label: "Mulher"
                                },
                                {
                                    value: "Ambos",
                                    label: "Ambos"
                                },
                                {
                                    value: "Outros",
                                    label: "Outros"
                                }
                            ]
                        }/>

                <Input name="maxAge" label="Idade MÃ¡xima"
                            value={maxAge}
                            onChange={
                                (e) => {
                                    setMaxAge(e.target.value)
                                }
                            }/>
                    <Input name="preference" label="Idade MÃ¡xima"
                            value={preference}
                            onChange={
                                (e) => {
                                    setPreference(e.target.value)
                                }
                            }/>

        <Input name="preference" label="DistÃ¢ncia"
                            value={preference}
                            onChange={
                                (e) => {
                                    setPreference(e.target.value)
                                }
                            }/>
                <button type="submit">
                    Buscar
                </button>

            </form>
        </PageHeader>

        <main> {
            peoples.map((people : People) => {
                return <PeopleItem key={
                        people.id
                    }
                    people={people}></PeopleItem>
        })
        } </main>

    </div>
    );
}

export default Peoples;
