import React, {FormEvent, useState} from "react";
import {useHistory} from "react-router-dom";

import PageHeader from "../../components/PageHeader";
import Input from "../../components/Input";
import Textarea from "../../components/Textarea";
import Select from "../../components/Select";

import warningIcon from "../../assets/images/icons/warning.svg";
import api from "../../services/api";

import "./styles.css";

export default function Register() {
    const history = useHistory();

    const [name, setName] = useState('')
    const [avatar, setAvatar] = useState('')
    const [whatsapp, setWhatsapp] = useState('')
    const [bio, setBio] = useState('')

    const [subject, setSubject] = useState('')
    const [cost, setCost] = useState('')

    const [scheduleItems, setScheduleItems] = useState([{
            week_day: 0,
            from: '',
            to: ''
        },]);

    function addNewScheduleItem() {
        setScheduleItems([
            ...scheduleItems, {
                week_day: 0,
                from: '',
                to: ''
            }
        ]);

    }

    function handleCreateClass(e : FormEvent) {
        e.preventDefault();

        api.post('classes', {
            name,
            avatar,
            whatsapp,
            bio,
            subject,
            cost: Number(cost),
            schedule: scheduleItems
        }).then(() => {
            alert("Cadastro realizado com sucesso!")

            history.push('/')
        }).catch(() => {
            alert("Erro ao realizar cadastro :(")
        })
    }

    function setScheduleItemValue(position : number, field : string, value : string) {
        const updateSchedule = scheduleItems.map((scheduleItem, index) => {
            if (index === position) {
                return {
                    ...scheduleItem,
                    [field]: value
                }
            }
            return scheduleItem;
        })
        setScheduleItems(updateSchedule);
    }

    return (
        <div className="container" id="page-teacher-form">
            <PageHeader title="Parece que você quer encontrar o amor da sua vida" description="O primeiro passo é preencher esse formulario de inscrição."/>

            <main>
                <form onSubmit={handleCreateClass}>
                    <fieldset>
                        <legend>Seus Dados</legend>
                        <Input name="name" label="Nome Completo"
                            value={name}
                            onChange={
                                (e) => {
                                    setName(e.target.value)
                                }
                            }/>
                        <Input name="avatar" label="Foto"
                            value={avatar}
                            onChange={
                                (e) => {
                                    setAvatar(e.target.value)
                                }
                            }/>
                        <Input name="whatsapp" label="Idade"
                            value={whatsapp}
                            onChange={
                                (e) => {
                                    setWhatsapp(e.target.value)
                                }
                            }/>
                        <Textarea name="bio" label="Biografia"
                            value={bio}
                            onChange={
                                (e) => {
                                    setBio(e.target.value)
                                }
                            }/>
                    </fieldset>

                <fieldset>
                    <legend>Preferências</legend>
                    <Select name="subject" label="Matéria"
                        value={subject}
                        onChange={
                            (e) => {
                                setSubject(e.target.value)
                            }
                        }
                        options={
                            [
                                {
                                    value: "Artes",
                                    label: "Artes"
                                },
                                {
                                    value: "Biologia",
                                    label: "Biologia"
                                },
                                {
                                    value: "Geografia",
                                    label: "Geografia"
                                },
                                {
                                    value: "Matematica",
                                    label: "Matematica"
                                }, {
                                    value: "Ingles",
                                    label: "Ingles"
                                }, {
                                    value: "Historia",
                                    label: "Historia"
                                }, {
                                    value: "Fisica",
                                    label: "Fisica"
                                }, {
                                    value: "Portugues",
                                    label: "Portugues"
                                }, {
                                    value: "Quimica",
                                    label: "Quimica"
                                },
                            ]
                        }/>
                    <Input name="cost" label="Custo da sua hora por aula"
                        value={cost}
                        onChange={
                            (e) => {
                                setCost(e.target.value)
                            }
                        }/>
                </fieldset>

            <fieldset>
                <legend>
                    Horários Disponiveis
                    <button type="button"
                        onClick={addNewScheduleItem}>+ Novo Horario</button>
                </legend>

                {
                scheduleItems.map((scheduleItem, index) => {
                    return (
                        <div key={
                                scheduleItem.week_day
                            }
                            className="schedule-item">
                            <Select name="week_day" label="Dia da Semana"
                                value={
                                    scheduleItem.week_day
                                }
                                onChange={
                                    e => setScheduleItemValue(index, 'week_day', e.target.value)
                                }
                                options={
                                    [
                                        {
                                            value: "0",
                                            label: "Domingo"
                                        },
                                        {
                                            value: "1",
                                            label: "Segunda"
                                        },
                                        {
                                            value: "2",
                                            label: "Terça"
                                        },
                                        {
                                            value: "3",
                                            label: "Quarta"
                                        }, {
                                            value: "4",
                                            label: "Quinta"
                                        }, {
                                            value: "5",
                                            label: "Sexta"
                                        }, {
                                            value: "6",
                                            label: "Sábado"
                                        },
                                    ]
                                }/>
                            <Input name="from" label="Das" type="time"
                                value={
                                    scheduleItem.from
                                }
                                onChange={
                                    e => setScheduleItemValue(index, 'from', e.target.value)
                                }/>
                            <Input name="to" label="Até" type="time"
                                value={
                                    scheduleItem.to
                                }
                                onChange={
                                    e => setScheduleItemValue(index, 'to', e.target.value)
                                }/>
                        </div>
                    );
                })
            } </fieldset>

            <footer>
                <p>
                    <img src={warningIcon}
                        alt="Aviso Importante"/>
                    Importante
                    <br/>
                    Preencha todos os dados
                </p>
                <button type="submit">Salvar cadastro</button>
            </footer>
        </form>
    </main>
</div>
    );
}
